// Format Email Data for OpenAI Summarization
// This node should come AFTER your email parser and BEFORE OpenAI

const items = $input.all();

console.log(`Formatting ${items.length} emails for OpenAI summarization`);

// Extract the actual email data
const emails = items.map(item => item.json);

// Get current date
const today = new Date().toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

console.log(`Processing emails for date: ${today}`);

// Sort emails by content score (highest first)
const sortedEmails = emails
  .filter(email => email.content && email.content.length > 10) // Only emails with real content
  .sort((a, b) => b.metadata.contentScore - a.metadata.contentScore);

console.log(`Filtered to ${sortedEmails.length} emails with substantial content`);

// Format each email for the prompt
const emailList = sortedEmails.map((email, index) => {
  return `EMAIL ${index + 1}:
Subject: ${email.subject}
From: ${email.from.name} (${email.from.domain})
Date: ${email.date}
Content Score: ${email.metadata.contentScore}
Content: ${email.content}

---`;
}).join('\n');

// Create the complete prompt with anti-hallucination safeguards
const prompt = `You are creating an AI newsletter digest. You must ONLY use the information provided below. Do NOT make up any information, dates, or events not explicitly mentioned in these emails.

TODAY'S DATE: ${today}

EMAIL DATA TO SUMMARIZE:
${emailList}

STRICT INSTRUCTIONS:
1. ONLY summarize information that appears in the above emails
2. Do NOT add external knowledge or make up events
3. Do NOT create fake dates, companies, or announcements
4. If an email lacks specific details, acknowledge the limitation
5. Group similar topics from the actual emails provided
6. Use the current date (${today}) in your digest title

Create a daily digest in this format:

# AI Daily Digest - ${today}

## üöÄ Major Releases
[Only include if mentioned in the provided emails]

## üî¨ Research & Breakthroughs
[Only include if mentioned in the provided emails]

## üìà Industry News
[Only include if mentioned in the provided emails]

## üõ†Ô∏è New Tools
[Only include if mentioned in the provided emails]

## üí° What to Watch
[Only trends you can infer from the provided content]

Rules:
- Maximum 2-3 sentences per item
- Include source attribution (newsletter name)
- Keep total under 400 words
- If you don't have enough content for a section, write "No significant updates in this category today"
- End with: "Sources: [list the actual newsletter names from the emails]"

REMEMBER: Use ONLY the content from the ${sortedEmails.length} emails provided above. Do not hallucinate or add external information.`;

// Log the prompt for debugging
console.log('Generated prompt preview:', prompt.substring(0, 500) + '...');
console.log(`Email sources included: ${sortedEmails.map(e => e.from.name).join(', ')}`);

// Return the formatted prompt
return [{ 
  json: { 
    prompt: prompt,
    email_count: sortedEmails.length,
    email_sources: sortedEmails.map(e => e.from.name),
    date: today,
    raw_emails: sortedEmails // Include for debugging if needed
  } 
}];

// Gmail Email Parser for n8n Code Node - Full Gmail API Response (Simplify=OFF)
// Handles rich Gmail API data structure with full content access
// Extracts and cleans email content for AI summarization

const items = $input.all();
const processedItems = [];

// INITIAL DEBUGGING - Let's see what we're working with
console.log(`\n=== FULL API RESPONSE ANALYSIS ===`);
console.log(`Total input items: ${items.length}`);
if (items.length > 0) {
  console.log('First item structure:');
  console.log('- Item keys:', Object.keys(items[0]));
  if (items[0].json) {
    console.log('- JSON keys:', Object.keys(items[0].json));
    console.log('- Has id:', !!items[0].json.id);
    console.log('- Has subject:', !!items[0].json.subject);
    console.log('- Has from:', !!items[0].json.from);
    console.log('- Has html:', !!items[0].json.html);
    console.log('- Has text:', !!items[0].json.text);
    console.log('- Has headers:', !!items[0].json.headers);
    console.log('- HTML content length:', items[0].json.html?.length || 0);
    console.log('- Text content length:', items[0].json.text?.length || 0);
  }
}

// Enhanced utility functions for content cleaning
const normalizeWhitespace = (text) => {
  if (!text) return '';
  return text
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .replace(/[ \t]+/g, ' ')
    .replace(/\u00A0/g, ' ') // Non-breaking spaces
    .trim();
};

const stripHTML = (html) => {
  if (!html) return '';

  // Remove HTML tags and decode entities
  return html
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '') // Remove scripts
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '') // Remove styles
    .replace(/<[^>]*>/g, ' ') // Remove HTML tags
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#8217;/g, "'")
    .replace(/&#8220;/g, '"')
    .replace(/&#8221;/g, '"')
    .replace(/&#8230;/g, '...')
    .replace(/&[#\w]+;/g, '') // Remove other entities
    .replace(/\s{2,}/g, ' ')
    .trim();
};

const parseDate = (dateString) => {
  try {
    if (!dateString) return new Date().toISOString();
    return new Date(dateString).toISOString();
  } catch (error) {
    console.log('Date parse error:', error.message);
    return new Date().toISOString();
  }
};

const extractSenderInfo = (fromField) => {
  if (!fromField) return { email: '', name: '', domain: '', isNewsletter: false };

  console.log('Parsing sender from field:', JSON.stringify(fromField, null, 2));

  let email = '';
  let name = '';

  // Handle different from field formats
  if (typeof fromField === 'string') {
    // Parse "Name <email@domain.com>" format or just "email@domain.com"
    const emailMatch = fromField.match(/<([^>]+)>/) || fromField.match(/([^\s<>]+@[^\s<>]+)/);
    email = emailMatch ? emailMatch[1].trim() : '';

    if (fromField.includes('<') && fromField.includes('>')) {
      name = fromField.split('<')[0].replace(/['"]/g, '').trim();
    }
  } else if (fromField.value && Array.isArray(fromField.value) && fromField.value.length > 0) {
    // Handle parsed email object format
    const firstSender = fromField.value[0];
    email = firstSender.address || '';
    name = firstSender.name || '';
  } else if (fromField.text) {
    // Handle text representation
    const emailMatch = fromField.text.match(/<([^>]+)>/) || fromField.text.match(/([^\s<>]+@[^\s<>]+)/);
    email = emailMatch ? emailMatch[1].trim() : '';

    if (fromField.text.includes('<') && fromField.text.includes('>')) {
      name = fromField.text.split('<')[0].replace(/['"]/g, '').trim();
    }
  }

  const domain = email.split('@')[1] || '';

  // Enhanced newsletter detection
  const newsletterPatterns = [
    /newsletter|digest|weekly|daily|update|news|bulletin/i,
    /noreply|no-reply|hello|hi|info|support/i,
    /automated|system|robot|bot/i,
    /substack|mailchimp|constantcontact/i
  ];

  const isNewsletter = newsletterPatterns.some(pattern => 
    pattern.test(name) || pattern.test(email) || pattern.test(domain)
  );

  return { email, name, domain, isNewsletter };
};

const calculateContentScore = (content, subject, sender, labelIds = []) => {
  let score = 0;

  // Length scoring (much more generous for full content)
  if (content.length > 5000) score += 5;
  else if (content.length > 2000) score += 4;
  else if (content.length > 1000) score += 3;
  else if (content.length > 500) score += 2;
  else if (content.length > 100) score += 1;

  // AI-related content scoring
  const aiKeywords = /\b(ai|artificial intelligence|machine learning|deep learning|neural|gpt|llm|openai|anthropic|chatgpt|automation|robotics|algorithm|data science|tech|innovation|model|claude|gemini)\b/gi;
  const aiMatches = (content + ' ' + subject).match(aiKeywords) || [];
  score += Math.min(aiMatches.length * 0.3, 4); // Cap AI keyword bonus

  // Newsletter bonus
  if (sender.isNewsletter) score += 1.5;

  // Subject line quality
  if (subject && subject.length > 10) score += 1;

  // Bonus for pre-filtered newsletters (Newsletter label)
  const hasNewsletterLabel = labelIds.some(labelId => 
    labelId.includes('Label_') || labelId.includes('Newsletter')
  );
  if (hasNewsletterLabel) score += 2; // Significant bonus for pre-filtered content

  return Math.round(score * 10) / 10; // Round to 1 decimal
};

const cleanContentForAI = (content, senderDomain = '') => {
  if (!content) return '';

  // Store original content for debugging
  const originalLength = content.length;
  console.log(`üßπ Cleaning content from ${senderDomain} (original: ${originalLength} chars)`);

  let cleaned = content
    // Remove those specific invisible characters
    .replace(/Õè/g, '') // Remove invisible separator characters
    .replace(/[\u0300-\u036F]/g, '') // Remove combining diacritical marks
    .replace(/[\u200B-\u200D\uFEFF]/g, '') // Zero-width characters
    .replace(/‚Äå/g, '') // Zero-width non-joiner
    .replace(/\s{2,}/g, ' '); // Multiple spaces to single space

  console.log(`üßπ After basic cleaning: ${cleaned.length} chars`);

  // Be less aggressive for Everyday AI emails
  if (senderDomain.includes('youreverydayai.com')) {
    console.log(`üßπ Using gentle cleaning for Everyday AI`);

    cleaned = cleaned
      // Only remove obvious footers
      .replace(/unsubscribe.{0,100}$/gi, '')
      .replace(/view this email in your browser.{0,100}/gi, '')

      // Clean excessive spacing
      .replace(/\n\s*\n\s*\n/g, '\n\n')
      .replace(/[-=_]{10,}/g, '---') // Only very long separator lines
      .trim();
  } else {
    // Normal aggressive cleaning for other newsletters
    cleaned = cleaned
      // Remove common newsletter footers and tracking
      .replace(/unsubscribe|privacy policy|terms of service.{0,300}$/gi, '')
      .replace(/view this email in your browser.{0,200}/gi, '')
      .replace(/forward to a friend.{0,200}/gi, '')
      .replace(/manage your preferences.{0,200}/gi, '')
      .replace(/sent to .+ because .+/gi, '')
      .replace(/powered by substack.{0,100}/gi, '')

      // Clean tracking URLs
      .replace(/https?:\/\/[^\s]+utm_[^\s]+/g, '[tracking-link]')
      .replace(/https?:\/\/[^\s]+\?[^\s]*ref=[^\s]*/g, '[ref-link]')
      .replace(/https?:\/\/substackcdn\.com\/[^\s]+/g, '[image]')

      // Remove template variables
      .replace(/\*\|[^|]*\|\*/g, '') // MailChimp
      .replace(/{{[^}]*}}/g, '') // Generic templates
      .replace(/%[A-Z_]+%/g, '') // Another template format

      // Clean excessive spacing and characters
      .replace(/\n\s*\n\s*\n/g, '\n\n')
      .replace(/[-=_]{5,}/g, '---') // Long separator lines
      .replace(/\s*\|\s*/g, ' | ') // Clean up table separators

      // Remove email signature patterns
      .replace(/--\s*\n[\s\S]*$/g, '') // Email signatures starting with --
      .replace(/best regards?.{0,200}$/gi, '')
      .replace(/thanks?.{0,100}$/gi, '')

      .trim();
  }

  console.log(`üßπ Final cleaned content: ${cleaned.length} chars (reduced by ${originalLength - cleaned.length})`);

  return cleaned;
};

// Main processing logic
console.log(`Starting processing of ${items.length} email items with full API data`);

items.forEach((item, index) => {
  try {
    const message = item.json;

    // DEBUGGING for full Gmail API format
    console.log(`\n=== Processing Full Email ${index + 1} ===`);
    console.log('Message keys:', Object.keys(message));

    // Extract data from full Gmail API response
    const id = message.id || `unknown_${index}`;
    const threadId = message.threadId || message.id || '';
    const labelIds = message.labelIds || [];

    // Extract core email fields
    const subject = message.subject || 'No Subject';
    const date = parseDate(message.date);

    console.log('Extracted metadata:', {
      subject: subject.substring(0, 50),
      date: date,
      labelIds: labelIds,
      hasHTML: !!message.html,
      hasText: !!message.text,
      htmlLength: message.html?.length || 0,
      textLength: message.text?.length || 0
    });

    const sender = extractSenderInfo(message.from);

    // Content extraction: prioritize text, fallback to HTML
    let content = '';
    let contentType = 'none';

    if (message.text && message.text.trim().length > 50) {
      content = normalizeWhitespace(message.text);
      contentType = 'text';
    } else if (message.html) {
      content = stripHTML(message.html);
      content = normalizeWhitespace(content);
      contentType = 'html-stripped';
    }

    // Clean content for AI processing
    content = cleanContentForAI(content, sender.domain);

    const contentScore = calculateContentScore(content, subject, sender, labelIds);

    const processedItem = {
      id,
      threadId,
      subject,
      from: {
        email: sender.email,
        name: sender.name,
        domain: sender.domain,
        full: message.from?.text || JSON.stringify(message.from) || ''
      },
      to: message.to?.text || JSON.stringify(message.to) || '',
      date: date,
      content: content,
      contentLength: content.length,
      contentType: contentType,
      metadata: {
        contentScore,
        hasAttachments: false, // Could be enhanced to detect attachments
        isMultipart: !!message.html && !!message.text,
        isNewsletter: sender.isNewsletter,
        labelIds: labelIds,
        processingMethod: 'full-gmail-api',
        sizeEstimate: message.sizeEstimate || 0,
        messageId: message.messageId || '',
        references: message.references || '',
        inReplyTo: message.inReplyTo || '',
        originalTextLength: message.text?.length || 0,
        originalHtmlLength: message.html?.length || 0,
        headers: Object.keys(message.headers || {}).length
      }
    };

    console.log(`Processed: "${subject.substring(0, 40)}" from ${sender.domain} (${content.length} chars, score: ${contentScore}, type: ${contentType})`);

    // More lenient filtering since we have full content
    if (processedItem.contentLength > 20 && contentScore >= 0.5) {
      processedItems.push({ json: processedItem });
    } else {
      console.log('Filtered out due to low content/score');
    }

  } catch (error) {
    console.error(`Error processing email ${index}:`, error);

    // Enhanced fallback item
    const fallbackItem = {
      id: item.json?.id || `error_${index}`,
      threadId: item.json?.threadId || '',
      subject: 'Parse Error',
      from: { email: 'unknown', name: '', domain: '', full: '' },
      to: '',
      date: new Date().toISOString(),
      content: item.json?.text || item.json?.html ? stripHTML(item.json.html || '') : 'Failed to parse email content',
      contentLength: (item.json?.text || item.json?.html || '').length,
      contentType: 'error',
      metadata: {
        error: error.message,
        contentScore: 0,
        hasAttachments: false,
        isMultipart: false,
        isNewsletter: false,
        labelIds: item.json?.labelIds || [],
        processingMethod: 'error'
      }
    };

    processedItems.push({ json: fallbackItem });
  }
});

// Enhanced summary logging
console.log(`\n=== Processing Summary ===`);
console.log(`Input: ${items.length} emails`);
console.log(`Output: ${processedItems.length} processed emails`);
console.log(`Success rate: ${((processedItems.length / items.length) * 100).toFixed(1)}%`);

processedItems.forEach((item, index) => {
  const { subject, contentLength, contentType, metadata } = item.json;
  console.log(`${index + 1}. "${subject.substring(0, 40)}" - ${contentLength} chars (${contentType}) [Score: ${metadata.contentScore}]`);
});

console.log(`\n=== Content Type Distribution ===`);
const contentTypes = processedItems.reduce((acc, item) => {
  const type = item.json.contentType;
  acc[type] = (acc[type] || 0) + 1;
  return acc;
}, {});
console.log(contentTypes);

return processedItems;

