console.log('Extracted markdown content length:', markdownContent.length);
console.log('First 200 chars:', markdownContent.substring(0, 200) + '...');

// Enhanced Markdown to HTML conversion
function convertMarkdownToHTML(markdown) {
  if (!markdown) return '<p>No content available</p>';

  let html = markdown
    // Convert headers (must be done in order from largest to smallest)
    .replace(/^### (.*$)/gmi, '<h3>$1</h3>')
    .replace(/^## (.*$)/gmi, '<h2>$1</h2>') 
    .replace(/^# (.*$)/gmi, '<h1>$1</h1>')

    // Convert emoji sections to styled headers
    .replace(/^(üöÄ.*$)/gmi, '<h2 class="section-header rocket">$1</h2>')
    .replace(/^(üî¨.*$)/gmi, '<h2 class="section-header research">$1</h2>')
    .replace(/^(üìà.*$)/gmi, '<h2 class="section-header industry">$1</h2>')
    .replace(/^(üõ†Ô∏è.*$)/gmi, '<h2 class="section-header tools">$1</h2>')
    .replace(/^(üí°.*$)/gmi, '<h2 class="section-header insights">$1</h2>')

    // Convert unordered lists
    .replace(/^- (.*$)/gmi, '<li>$1</li>')

    // Convert bold text
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')

    // Convert italic text
    .replace(/\*(.*?)\*/g, '<em>$1</em>')

    // Convert inline code
    .replace(/`(.*?)`/g, '<code>$1</code>')

    // Handle line breaks and paragraphs
    .replace(/\n\n+/g, '</p><p>')
    .replace(/\n/g, '<br>');

  // Wrap list items in ul tags
  html = html.replace(/(<li>.*<\/li>)/s, (match) => {
    // Check if already wrapped
    if (html.includes('<ul>')) return match;
    return '<ul>' + match.replace(/<\/li><li>/g, '</li><li>') + '</ul>';
  });

  // Wrap everything in paragraph tags if not already wrapped
  if (!html.startsWith('<h1>') && !html.startsWith('<h2>') && !html.startsWith('<div>')) {
    html = '<p>' + html + '</p>';
  }

  return html;
}

// Convert the markdown to HTML
